import { Snowflake } from "discord-api-types/globals";

export const Errors = {
    GuildCommandAlreadyRegistered: (
        commandName: string,
        guildId: Snowflake
    ): Error => {
        return new Error(
            `The command '${commandName}' has already been registered in the guild scope '${guildId}'.`
        );
    },
    ModuleWithNameAlreadyRegistered: (
        commandName: string,
        guildId: Snowflake
    ): Error => {
        return new Error(
            `The command '${commandName}' has already been registered in the guild scope '${guildId}'.`
        );
    }
};
