import { Guild } from "discord.js";

import {
    ApplicationCommand,
    ApplicationCommandOptions
} from "@structs/ApplicationCommand";
import { Module } from "@structs/Module";

export interface GuildApplicationCommandOptions
    extends ApplicationCommandOptions {
    guild: Guild;
}

export abstract class GuildApplicationCommand extends ApplicationCommand {
    protected constructor(
        module: Module,
        public override readonly options: GuildApplicationCommandOptions
    ) {
        super(module, options);
    }
}
